as = @(x) x+exp(1)/(exp(1).^2-1)*(exp(1).^(-x)-exp(1).^x);n = 9; h = 0.1; e = ones(n,1);A = spdiags([-e e*(2+h^2) -e], -1:1, n, n);b = h^2*(0.1:0.1:0.9)';y = A\b; y = [0;y;0]';% plot([0:0.1:1], y, [0:0.1:1], as([0:0.1:1]))for t = [0:0.1:1]	fprintf('%.1f & %f & %f & %f\\\\\n', t, y(1+t*10), as(t), abs(y(1+t*10)-as(t)));end