as = @(x) x+exp(1)/(exp(1).^2-1)*(exp(1).^(-x)-exp(1).^x);f = @(x,y) [y(2); -x+y(1)];[zx, zy] = RK4(f,[0 1],[0;1],10);[ux, uy] = RK4(f,[0 1],[0;0],10);A = (0-uy(end,1))/zy(end,1);ns = @(x) A*zy(x,1) + uy(x,1);% plot([0:0.1:1], ns([1:1:11]), [0:0.1:1], as([0:0.1:1]))for t = [0:0.1:1]	fprintf('%.1f & %f & %f & %f\\\\\n', t, ns(10*t+1), as(t), abs(ns(10*t+1)-as(t)));endfunction [T, Y] = RK4(f,tspan,y0,n)	a = tspan(1); b = tspan(2); h = (b-a)/n;	t = a; T = a;	y = y0; Y = y0';	for i = 1:n		k1 = f(t,y);		k2 = f(t+h/2,y+h/2*k1);		k3 = f(t+h/2,y+h/2*k2);		k4 = f(t+h,y+h*k3);		y = y + h/6*(k1+2*k2+2*k3+k4);		t = t+h; T = [T; t]; Y = [Y; y'];	endend